content:
{
  guid: {
    type: DataTypes.STRING,
    primaryKey: true,
    allowNull: false
  },
  productModel: { type: DataTypes.STRING, allowNull: false },
  productName: { type: DataTypes.STRING, allowNull: false },
  schemaId: { type: DataTypes.STRING }, // 物模型guid

  productParam: DataTypes.JSON, // 属性信息
  productMethod: DataTypes.JSON, // 方法信息

  data1: DataTypes.JSON, // 扩展数据

  tag: DataTypes.STRING,
  remark: DataTypes.TEXT,

  // 记录状态，默认0，-1表示被删除
  recordState: {
    type: DataTypes.INTEGER,
    allowNull: false,
    defaultValue: 0
  }
}

{
  guid: {
    type: DataTypes.STRING,
    primaryKey: true,
    allowNull: false
  },
  productModel: { type: DataTypes.STRING, allowNull: false }, // 产品型号
  serialNumber: { type: DataTypes.STRING, allowNull: false }, // 设备编号，工厂设置

  connectAddress: DataTypes.TEXT, // 连接地址
  cloudDeviceName: { type: DataTypes.STRING, unique: true }, // 物接入设备名字--唯一,可以由设备[型号-序列号-随机字符串]构成
  groupId: { type: DataTypes.STRING }, // 设备分组

  isActivated: {
    type: DataTypes.INTEGER,
    allowNull: false,
    defaultValue: 0
  },  // 设备首次连接后激活，只允许激活一次

  data1: DataTypes.JSON, // 扩展数据

  tag: DataTypes.STRING,
  remark: DataTypes.TEXT,

  // 记录状态，默认0，-1表示被删除
  recordState: {
    type: DataTypes.INTEGER,
    allowNull: false,
    defaultValue: 0
  }
}

{
  guid: {
    type: DataTypes.STRING,
    primaryKey: true,
    allowNull: false
  },
  name: { type: DataTypes.STRING, allowNull: false },
  priority: { type: DataTypes.INTEGER, allowNull: false },

  productModel: DataTypes.STRING,

  rule: DataTypes.JSON, // 告警规则，参照sequelize的where定义
  data1: DataTypes.JSON, // 扩展数据

  tag: DataTypes.STRING,
  remark: DataTypes.TEXT,

  // 记录状态，默认0，-1表示被删除
  recordState: {
    type: DataTypes.INTEGER,
    allowNull: false,
    defaultValue: 0
  }
}

{
  guid: {
    type: DataTypes.STRING,
    primaryKey: true,
    allowNull: false
  },
  deviceId: { type: DataTypes.STRING, allowNull: false }, // 设备的guid
  serialNumber: { type: DataTypes.STRING, allowNull: false },
  productModel: { type: DataTypes.STRING, allowNull: false },

  alarmRuleId: { type: DataTypes.STRING, allowNull: false },
  alarmName: { type: DataTypes.STRING, allowNull: false },
  alarmContent: { type: DataTypes.JSON, allowNull: false },

  priority: DataTypes.INTEGER,  // 告警的优先级别
  alarmState: {
    type: DataTypes.INTEGER,
    allowNull: false,
    defaultValue: 0
  }, // 异常的处理状态，0-未处理，1-处理中，2-关闭，...

  data1: DataTypes.JSON, // 扩展数据

  tag: DataTypes.STRING,
  remark: DataTypes.TEXT,

  // 记录状态，默认0，-1表示被删除
  recordState: {
    type: DataTypes.INTEGER,
    allowNull: false,
    defaultValue: 0
  }
}

{
  guid: {
    type: DataTypes.STRING,
    primaryKey: true,
    allowNull: false
  },
  deviceId: { type: DataTypes.STRING, allowNull: false }, // 设备的guid
  productModel: { type: DataTypes.STRING, allowNull: false }, // 设备型号
  serialNumber: { type: DataTypes.STRING, allowNull: false }, // 设备编号

  level: DataTypes.INTEGER,  // 0-info 1-warn 2-error
  code: DataTypes.STRING,  // 异常编码，可能含有字符
  message: DataTypes.TEXT,
  stack: DataTypes.TEXT,

  issueState: {
    type: DataTypes.INTEGER,
    allowNull: false,
    defaultValue: 0
  }, // 异常的处理状态，0-未处理，1-处理中，2-关闭，...

  data1: DataTypes.JSON, // 扩展数据

  tag: DataTypes.STRING,
  remark: DataTypes.TEXT,

  // 记录状态，默认0，-1表示被删除
  recordState: {
    type: DataTypes.INTEGER,
    allowNull: false,
    defaultValue: 0
  }
}
